    const handleEdit = () => {
        onEdit(post);
    }
}

    return (
            <article>
                   <h2>{post.title}</h2>
                  <p>{post.body}</p>
                  <button onClick={()=> setEditing(true)}>Editar </button>
                  <button onClick={handleDelete}>Borrar </button>

            </article>
    )

}

