    const id = newpost.length;
    post.id = id;
    setPosts([...newpost,post]); {/* este viene del postform variable "post"onSubmit (post) */}


}


const deletePost = (post) =>{
    setPosts(newpost.filter((element) => element.id !== post.id))
    }



const editPost =(editedPost) =>{
    const newPosts = [...newpost];
    newPosts[editedPost.id] = editedPost;
    setPosts(newPosts);
}


    return (
      <div>
            <section>
            <h1>Blog</h1>
                {newpost.map((element, index) => (
                    <Post key={index} id={index} post={element} onDelete={deletePost} onEdit={editPost}/>
                ))}
            </section>
            <section>
                <h1>Crea tu blog </h1>
                <PostForm onSubmit={addPost}/>
            </section>
            </div>
    );
};

export default Blog;
