import { useState } from "react";
import Post from "./Post";
import PostForm from "./PostForm";

const Blog = () => {
    const [newpost, setPosts]= useState([{title: "test", body: "prueba"}]);

const addPost = (post) =>{
    setPosts([...newpost,post]); {/* este viene del postform valriable "post"onSubmit (post) */}
}


const deletePost = (post) =>{
    setPosts(newpost.filter((element) => element !== post))
    }

const editPost =(editedPost,index) =>{
    const newPosts = [...newpost];
    newPosts[index] = editedPost;
    setPosts(newPosts);
}


    return (
      <div>
            <section>
            <h1>Blog</h1>
                {newpost.map((element, index) => (
                    <Post key={index} id={index} post={element} onDelete={deletePost} onEdit={editPost}/>
                ))}
            </section>
            <section>
                <h1>Crea tu blog </h1>
                <PostForm onSubmit={addPost}/>
            </section>
            </div>
    );
};

export default Blog;
