import { useState } from "react";
import Post from "./Post";
import PostForm from "./PostForm";

const Blog = () => {
    const [posts, setPosts]= useState([{title: "test", body: "prueba"}]);

const addPost = (post) =>{
    setPosts([...posts,...post]);
}
    return (
      <div>
            <h1>Blog</h1>
            <section>
                {posts.map((post, index) => (
                    <Post key={index}post={post}/>
                ))}
            </section>
            <section>
                <PostForm onSubmit={addPost}/>
            </section>
            </div>
    );
};

export default Blog;
